// store-actions-api.js

import dispatcher from  "./dispatcher"; import { Action } from  "./api"; import { Api } from  "./api"; import {  CREATE_PRODUCT,
  GET_PRODUCTS,
  REMOVE_PRODUCT,
  SELECT_INDEX
} from  "./product.constants"; 
let store = {}; 
class Store extends EventEmitter {  constructor() {}
  addListener(listener) {
    this.on("changed", listener);
  }

  emitChange() {
    this.emit("changed");
  }

  emitError() {
    this.emit("error");
  }

  getSelectedItem() {
    return store["selectedItem"];
  }
}

const api =  new Api(); const storeInstance =  new Store(); 
const selectIndex = index => {  store["selectedIndex"] = index; };

const createProduct = product => {  if (!store["products"]) {
    store["products"] = [];  }
  store.products = [...store.products, Object.assign(product)];  };

const removeProduct = product => {  if (!store["products"]) return;
  store["products"] = products.filter(p => p.id !== product.id); };

const setProducts = products => {  store["products"] = products; };

const setError = error => {  store["error"] = error; };

dispatcher.register(async ({ type, data }) => {  switch (type) {    case  "SELECT_INDEX":
      selectIndex(message.data);
      storeInstance.emitChange();
      break;
    case CREATE_PRODUCT:      try {        await api.createProduct(data);        storeInstance.emitChange();
      } catch (error) {        setError(error);
        storeInstance.emitError();
      }
      break;
    case GET_PRODUCTS:      try {        const products =  await api.getProducts();        setProducts(products);
        storeInstance.emitChange();
      } catch (error) {        setError(error);
        storeInstance.emitError();
      }
      break;
   }
});
